  <boltAction type="file" filePath="src/App.tsx">import { useEffect } from 'react'
import { useStore } from './store'
import ChannelTabs from './components/ChannelTabs'
import ConversationList from './components/ConversationList'
import ChatHeader from './components/ChatHeader'
import MessageArea from './components/MessageArea'
import MessageInput from './components/MessageInput'
import ThemeToggle from './components/ThemeToggle'
import AgentControls from './components/AgentControls'

const App: React.FC = () =&gt; {
  const { selectedConversation, fetchConversations, fetchMessages, theme, setTheme } = useStore()

  useEffect(() =&gt; {
    fetchConversations()
    const interval = setInterval(fetchConversations, 10000)
    return () =&gt; clearInterval(interval)
  }, [fetchConversations])

  useEffect(() =&gt; {
    if (selectedConversation) {
      fetchMessages(selectedConversation)
      const msgInterval = setInterval(() =&gt; fetchMessages(selectedConversation), 10000)
      return () =&gt; clearInterval(msgInterval)
    }
  }, [selectedConversation, fetchMessages])

  useEffect(() =&gt; {
    document.documentElement.setAttribute('data-theme', theme)
  }, [theme])

  return (
    &lt;div className="flex h-screen overflow-hidden"&gt;
      &lt;div className="w-1/4 border-r border-[var(--border)] flex flex-col"&gt;
        &lt;div className="p-4 flex justify-between items-center"&gt;
          &lt;h1 className="text-xl font-bold"&gt;Conversations&lt;/h1&gt;
          &lt;ThemeToggle /&gt;
        &lt;/div&gt;
        &lt;ChannelTabs /&gt;
        &lt;ConversationList /&gt;
      &lt;/div&gt;
      &lt;div className="flex-1 flex flex-col"&gt;
        {selectedConversation ? (
          &lt;&gt;
            &lt;ChatHeader /&gt;
            &lt;MessageArea /&gt;
            &lt;div className="p-4 border-t border-[var(--border)] flex items-center"&gt;
              &lt;MessageInput /&gt;
              &lt;AgentControls /&gt;
            &lt;/div&gt;
          &lt;/&gt;
        ) : (
          &lt;div className="flex-1 flex items-center justify-center text-[var(--text-secondary)]"&gt;
            Select a conversation to start chatting
          &lt;/div&gt;
        )}
      &lt;/div&gt;
    &lt;/div&gt;
  )
}

export default App</boltAction>

  <boltAction type="file" filePath="src/store.ts">import { create } from 'zustand'
import axios from 'axios'

const CHATWOOT_CONFIG = {
  baseURL: 'https://devchatwoot.aivence.com/api/v1',
  apiKey: 'nZhTYqBV3E5TUnmRcddoEnSU',
  accountId: '1',
  headers: {
    'api_access_token': 'nZhTYqBV3E5TUnmRcddoEnSU',
    'Content-Type': 'application/json'
  }
}

export type Conversation = {
  id: number
  account_id: number
  inbox_id: number
  status: string
  unread_count: number
  contact: {
    id: number
    name: string
    email: string
    phone_number: string
    avatar_url: string
  }
  inbox: {
    id: number
    name: string
    channel_type: string
  }
  messages: Message[]
}

export type Message = {
  id: number
  content: string
  message_type: 'incoming' | 'outgoing'
  created_at: string
  sender: {
    name: string
    avatar_url: string
  }
  attachments: { file_type: string; data_url: string }[]
}

type State = {
  conversations: Conversation[]
  selectedConversation: number | null
  messages: Message[]
  loading: boolean
  error: string | null
  theme: 'light' | 'dark'
  selectedChannel: string
  setSelectedConversation: (id: number | null) =&gt; void
  fetchConversations: () =&gt; Promise&lt;void&gt;
  fetchMessages: (conversationId: number) =&gt; Promise&lt;void&gt;
  sendMessage: (conversationId: number, content: string) =&gt; Promise&lt;void&gt;
  setTheme: (theme: 'light' | 'dark') =&gt; void
  setSelectedChannel: (channel: string) =&gt; void
  pauseAgent: (conversationId: number) =&gt; Promise&lt;void&gt;
  resumeAgent: (conversationId: number) =&gt; Promise&lt;void&gt;
}

export const useStore = create&lt;State&gt;((set, get) =&gt; ({
  conversations: [],
  selectedConversation: null,
  messages: [],
  loading: false,
  error: null,
  theme: 'light',
  selectedChannel: 'all',
  setSelectedConversation: (id) =&gt; set({ selectedConversation: id }),
  setTheme: (theme) =&gt; set({ theme }),
  setSelectedChannel: (channel) =&gt; set({ selectedChannel: channel }),
  fetchConversations: async () =&gt; {
    set({ loading: true, error: null })
    try {
      const response = await axios.get(
        `${CHATWOOT_CONFIG.baseURL}/accounts/${CHATWOOT_CONFIG.accountId}/conversations`,
        { headers: CHATWOOT_CONFIG.headers }
      )
      set({ conversations: response.data.data || [] })
    } catch (err) {
      set({ error: 'Failed to fetch conversations' })
    } finally {
      set({ loading: false })
    }
  },
  fetchMessages: async (conversationId) =&gt; {
    set({ loading: true, error: null })
    try {
      const response = await axios.get(
        `${CHATWOOT_CONFIG.baseURL}/accounts/${CHATWOOT_CONFIG.accountId}/conversations/${conversationId}/messages`,
        { headers: CHATWOOT_CONFIG.headers }
      )
      set({ messages: response.data.data || [] })
    } catch (err) {
      set({ error: 'Failed to fetch messages' })
    } finally {
      set({ loading: false })
    }
  },
  sendMessage: async (conversationId, content) =&gt; {
    try {
      await axios.post(
        `${CHATWOOT_CONFIG.baseURL}/accounts/${CHATWOOT_CONFIG.accountId}/conversations/${conversationId}/messages`,
        { content },
        { headers: CHATWOOT_CONFIG.headers }
      )
      const { fetchMessages } = get()
      fetchMessages(conversationId)
    } catch (err) {
      set({ error: 'Failed to send message' })
    }
  },
  pauseAgent: async (conversationId) =&gt; {
    try {
      await fetch('/api/webhook/pause', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ conversation_id: conversationId })
      })
    } catch (err) {
      set({ error: 'Failed to pause agent' })
    }
  },
  resumeAgent: async (conversationId) =&gt; {
    try {
      await fetch('/api/webhook/resume', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ conversation_id: conversationId })
      })
    } catch (err) {
      set({ error: 'Failed to resume agent' })
    }
  }
}))</boltAction>

