  <boltAction type="file" filePath="src/components/ConversationList.tsx">import { useStore } from '../store'
import ConversationItem from './ConversationItem'
import { Loader2 } from 'lucide-react'

const ConversationList: React.FC = () =&gt; {
  const { conversations, loading, error, selectedChannel } = useStore()

  const filteredConversations = selectedChannel === 'all'
    ? conversations
    : conversations.filter(conv =&gt; conv.inbox.channel_type.toLowerCase().includes(selectedChannel))

  if (loading) return &lt;div className="flex justify-center p-4"&gt;&lt;Loader2 className="animate-spin" /&gt;&lt;/div&gt;
  if (error) return &lt;div className="p-4 text-red-500"&gt;{error}&lt;/div&gt;
  if (filteredConversations.length === 0) return &lt;div className="p-4 text-[var(--text-secondary)] text-center"&gt;No conversations yet&lt;/div&gt;

  return (
    &lt;div className="overflow-y-auto flex-1"&gt;
      {filteredConversations.map(conv =&gt; (
        &lt;ConversationItem key={conv.id} conversation={conv} /&gt;
      ))}
    &lt;/div&gt;
  )
}

export default ConversationList</boltAction>

